import React, { useState, useEffect } from 'react';
import './App.css';
import ImageCarousel from './ImageCarousel';
import InformationDivs from './InformationDiv';

const App = () => {
    const [selectedTask, setSelectedTask] = useState('');
    const [tasks, setTasks] = useState([]);

    const handleTaskSelection = (task) => {
        setSelectedTask(task);
    };

    useEffect(() => {
        const fetchTasks = async () => {
            try {
                const response = await fetch('http://localhost:3000/api/tasks');
                const data = await response.json();
                setTasks(data);
            } catch (error) {
                console.error('Error fetching tasks:', error);
            }
        };

        fetchTasks();
    }, []);

    return (
        <div className="container">
            <div className="button-container">
                <button onClick={() => handleTaskSelection('Task 1')}>Task 1</button>
                <button onClick={() => handleTaskSelection('Task 2')}>Task 2</button>
                <button onClick={() => handleTaskSelection('Task 3')}>Task 3</button>
            </div>
            <ImageCarousel />
            <InformationDivs />
            {selectedTask && <h2>You selected: {selectedTask}</h2>}
            <div>
                <h3>Tasks from Database:</h3>
                <ul>
                    {tasks.map(task => (
                        <li key={task.id}>{task.name}</li> // Adjust according to your DB structure
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default App;
